    private void btnsomaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnsomaActionPerformed
        
        // 1 passo - criar um objeto da classe soma
        Soma obj = new Soma();
        
        // 2 passo - pegar os valores
        obj.setV1 (Double.parseDouble(txtnm1.getText()));
        obj.setV2 (Double.parseDouble(txtnm2.getText()));
        
        // 3 passo - chamar o m√©todo calcularsoma
        obj.calcularsoma();
        
        // 4 passo - mostrar o resultado
        txttotal.setText(String.valueOf(obj.getTotal()));
    }//GEN-LAST:event_btnsomaActionPerformed

